import './App.css';
import React, { useState, useEffect } from 'react'
import Usd from './components/Usd';

function App() {

  const [state, setstate] = useState({
    currency_1 : "",
    currency_2 : "",
    input_1 : ""
  })

  const [dataOperationcomplete, setDataSend] = useState({
    general_info : "",
    complete_info : ""
  })

  const obj = {
    usd : 80,
    inr : 1,
    euro : 100,
    aed : 20,
    cad : 30,

  }

  const getData =  (data) =>{
    console.log(data," inside app.js / parent")
    setstate(data) 
    console.log(data.currency_1)
    let my_general_calc = obj[data.currency_1] / obj[data.currency_2]
    let my_complete_calc = my_general_calc * Number(data.input_1)

    console.log("before sendData")

     setDataSend({
      general_info : `Value of 1 ${data.currency_1} is equivalent to ${my_general_calc} ${data.currency_2}...`,
      complete_info : `Value of ${obj[data.input_1]} ${data.currency_1} is equivalent to ${my_complete_calc} ${state.currency_2}...`
    })
    console.log("after  sendData")
    console.log(my_general_calc,my_complete_calc)
    console.log(`Value of 1 ${data.currency_1} is equivalent to ${my_general_calc} ${data.currency_2}...`)
    console.log(`Value of ${obj[data.input_1]} ${data.currency_1} is equivalent to ${my_complete_calc} ${state.currency_2}...`)
    console.log(dataOperationcomplete.complete_info)
    console.log(dataOperationcomplete.general_info)


  }

  useEffect(() => {
  },[dataOperationcomplete]);


  return (
    <>
      <h1 className='my-center-style'>Welcome to currency converter!!!</h1>
      <Usd getData={getData} dataOperationcomplete={dataOperationcomplete}/>
    </>
  );
}

export default App;
